frappe.ui.form.on('Sales Order', {
	refresh(frm) {

        cur_frm.add_custom_button(__('Create Customer Feedback'), function() {
            frappe.model.with_doctype('Customer  Feedback', function() {
                var feedback = frappe.model.get_new_doc('Customer  Feedback');
                feedback.sales_order = cur_frm.doc.name;
                feedback.customer_name = cur_frm.doc.customer_name;
                feedback.customer = cur_frm.doc.customer;
                feedback.custom_purchase_order_number = cur_frm.doc.po_no;
                feedback.delivery_note_date = cur_frm.doc.delivery_date;
                frappe.set_route('Form', feedback.doctype, feedback.name);
            });
        }, __("Create"));
    }
});

frappe.ui.form.on('Customer Feedback', {
	refresh(frm) {
	if (cur_frm.doc.sales_order && cur_frm.is_new()) {
    frappe.db.get_doc('Sales Order', cur_frm.doc.sales_order)
        .then(doc => {

            doc.items.forEach(function (item) {
                var feedback_item = cur_frm.add_child("items");
                feedback_item.item_code = item.item_code;
                feedback_item.item_name = item.item_name;
                feedback_item.qty = item.qty;
                feedback_item.rate = item.rate;
                feedback_item.description = item.description;
                feedback_item.uom = item.uom;
                 feedback_item.amount = item.amount;
                feedback_item.delivery_date = item.delivery_date;


            });
            cur_frm.refresh_fields()

        })
}
	}
})

frappe.ui.form.on('Job Card Time Log', {
    time_in_mins(frm, cdt, cdn) {
        let row = frappe.get_doc(cdt, cdn);
        console.log(frm.doc.time_logs)
        let time = 0
        for (let i=0;i< frm.doc.time_logs.length;i++){
            time += frm.doc.time_logs[i].time_in_mins
        }
        frm.set_value("custom_total_time_in_mins",time)
    }
})

frappe.ui.form.on('Packing Slip', {
    refresh: function(frm) {
        let packetCount = 0;
        let quantityCount = 0;

        if (frm.doc.custom_packing_list_items) {
            frm.doc.custom_packing_list_items.forEach(item => {
                packetCount += item.pkts || 0;
                quantityCount += item.qty || 0;
            });
        }
        frm.set_value('custom_toatal_pkts', packetCount);
        frm.set_value('custom_total_qty', quantityCount);
    }
});

frappe.ui.form.on("Stock Entry", {
    before_submit: function(frm) {
        // Check if the Stock Entry type is not 'Material Receipt'
        if (frm.doc.purpose !== "Material Receipt") {
            let items_to_request = [];

            frm.doc.items.forEach((item) => {
                frappe.call({
                    method: "frappe.client.get_value",
                    args: {
                        doctype: "Bin",
                        filters: {
                            item_code: item.item_code,
                            warehouse: item.s_warehouse || item.t_warehouse,
                        },
                        fieldname: "actual_qty"
                    },
                    callback: function(r) {
                        if (r.message) {
                            let available_qty = r.message.actual_qty || 0;
                            if (available_qty < item.qty) {
                                items_to_request.push({
                                    item_code: item.item_code,
                                    item_name: item.item_name,
                                    required_qty: item.qty - available_qty
                                });
                            }
                        }
                    },
                    async: false
                });
            });

            if (items_to_request.length > 0) {
                // Create an HTML table to display the items in the popup
                let message = `<p><b>The following items are not available in sufficient quantity:</b></p>
                               <table class="table table-bordered">
                                   <thead>
                                       <tr>
                                           <th><b>Item Code</b></th>
                                           <th><b>Item Name</b></th>
                                           <th><b>Requested Quantity</b></th>
                                       </tr>
                                   </thead>
                                   <tbody>`;

                items_to_request.forEach(item => {
                    message += `<tr>
                                    <td>${item.item_code}</td>
                                    <td>${item.item_name}</td>
                                    <td>${item.required_qty}</td>
                                </tr>`;
                });

                message += `</tbody></table><p>Do you want to create a Material Request for these items?</p>`;

                frappe.confirm(
                    message,
                    function() {
                        create_material_request(frm, items_to_request); // Create Material Request and redirect
                    },
                    function() {
                        frappe.msgprint("Stock Entry submission canceled.");
                        frappe.validated = false;
                    }
                );
            }
        }
    }
});

function create_material_request(frm, items) {
    frappe.call({
        method: "frappe.client.insert",
        args: {
            doc: {
                doctype: "Material Request",
                material_request_type: "Purchase",
                items: items.map(item => ({
                    item_code: item.item_code,
                    qty: item.required_qty,
                    warehouse: "Stores - SAPL",  // Set the target warehouse for each item
                    schedule_date: frappe.datetime.now_date()
                }))
            }
        },
        callback: function(response) {
            // Redirect to the new Material Request document immediately
            frappe.set_route("Form", "Material Request", response.message.name);
        }
    });
}

frappe.ui.form.on('Service Visit Register', {
    refresh: function(frm) {
        frm.add_custom_button(__('Calculate To Pay'), function() {
            // Get the 'advancetaken' and 'total_expense' values
            var advancetaken = frm.doc.advancetaken || 0;
            var totalExpense = frm.doc.total_expense || 0;

            // Calculate 'to_pay' by subtracting 'advancetaken' from 'total_expense'
            var toPay = totalExpense - advancetaken;

            // Update the 'to_pay' field
            frm.set_value('to_pay', toPay);
        });

         // Add a custom button to trigger the calculation
         frm.add_custom_button(__('Calculate Total Expense'), function() {
            calculateTotalExpense(frm);
        });

        frm.fields_dict['service_visit_manday_reporting'].grid.get_field('technician_cost').get_query = function(doc, cdt, cdn) {
            var row = locals[cdt][cdn];
            return {
                filters: {
                    'monthly_wages': row.monthly_wages,
                    'number_of_days_worked': row.number_of_days_worked,
                    'extra_days_absent': row.extra_days_absent,
                }
            };
        };

        // Add a custom button to trigger the calculation
        frm.add_custom_button(__('Calculate Total Service Cost'), function() {
            calculateTotalServiceCost(frm);
        });

        // Add a custom button to trigger the calculation
        frm.add_custom_button(__('Calculate Total Technician Cost'), function() {
            calculateTotalTechnicianCost(frm);
        });

    },
    sales_order_reference: function(frm) {
        // Listen for changes in the Sales Order Reference field
        // Fetch and set data when the Sales Order Reference changes
        fetch_and_set_sales_order_data(frm);
    }
});


function calculateTotalExpense(frm) {
    let totalExpense = 0;

    frm.doc.expense_reporting.forEach(function(row) {
        totalExpense += row.cost || 0;
    });

    frm.set_value('total_expense', totalExpense);
    frm.refresh_field('total_expense');
}


function fetch_and_set_sales_order_data(frm) {
    var sales_order_reference = frm.doc.sales_order_reference;
    if (sales_order_reference) {
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Sales Order',
                name: sales_order_reference,
                filters: {
                    fields: ['items']
                }
            },
            callback: function(r) {
                if (r.message && r.message.items) {
                    var items = r.message.items;
                    var purpose_table = frm.doc.purposes || [];

                    // Clear existing rows in the child table
                    purpose_table.splice(0, purpose_table.length);

                    // Loop through the fetched data and add it to the 'purposes' table
                    $.each(items, function(i, item) {
                        var child_row = frappe.model.add_child(frm.doc, 'Service Visit Purpose', 'purposes');
                        child_row.item_code = item.item_code;
                        child_row.item_name = item.item_name;
                        child_row.description = item.description;
                    });

                    frm.refresh_field('purposes');
                }
            }
        });
    }
}

function calculateTotalServiceCost(frm) {
    // Get the values of the custom fields
    var totalTechnicianCost = frm.doc.total_technician_cost || 0;
    var totalExpense = frm.doc.total_expense || 0;

    // Calculate the total service cost
    var totalServiceCost = totalTechnicianCost + totalExpense;

    // Update the 'total_service_cost' field with the calculated total
    frm.set_value('total_service_cost', totalServiceCost);
    frm.refresh_field('total_service_cost');
}

function calculateTotalTechnicianCost(frm) {
    let totalTechnicianCost = 0;

    if (frm.doc.service_visit_manday_reporting){

        frm.doc.service_visit_manday_reporting.forEach(function(row) {
            totalTechnicianCost += row.technician_cost || 0;
        });
    }

    frm.set_value('total_technician_cost', totalTechnicianCost);
    frm.refresh_field('total_technician_cost');
}



frappe.ui.form.on('Service Visit Manday Reporting', {
    monthly_wages: function(frm, cdt, cdn) {
        calculateTechnicianCost(frm, cdt, cdn);
    },
    number_of_days_worked: function(frm, cdt, cdn) {
        calculateTechnicianCost(frm, cdt, cdn);
    },
    extra_days_absent: function(frm, cdt, cdn) {
        calculateTechnicianCost(frm, cdt, cdn);
    }
});

function calculateTechnicianCost(frm, cdt, cdn) {
    var row = locals[cdt][cdn];
    if (row.monthly_wages && row.number_of_days_worked && row.extra_days_absent) {
        var mw = row.monthly_wages;
        var nd = row.number_of_days_worked;
        var ex = row.extra_days_absent;
        var technician_cost = (mw / 26) * nd * 1.5 + ex * (mw / 26);
        frappe.model.set_value(cdt, cdn, 'technician_cost', technician_cost);
    } else {
        frappe.model.set_value(cdt, cdn, 'technician_cost', 0);
    }
}

// ðŸ”„ Recalculate amount when qty or rate is changed
frappe.ui.form.on('Proforma Invoice Item', {
    quantity: function (frm, cdt, cdn) {
        calculate_amount(frm, cdt, cdn);
        calculate_totals(frm); // also update parent
    },
    rate: function (frm, cdt, cdn) {
        calculate_amount(frm, cdt, cdn);
        calculate_totals(frm);
    },
    amount: function(frm, cdt, cdn) {
        calculate_totals(frm);  // if amount is edited manually
    }
});

// ðŸ“¦ Calculate line amount
function calculate_amount(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    row.amount = (row.quantity || 0) * (row.rate || 0);
    frappe.model.set_value(cdt, cdn, 'amount', row.amount);
}

// ðŸ” Calculate parent totals
function calculate_totals(frm) {
    let total_qty = 0;
    let total_amt = 0;

    (frm.doc.custom_items || []).forEach(row => {
        total_qty += row.quantity || 0;
        total_amt += row.amount || 0;
    });

    // Show in UI
    frm.set_value('total_quantity', total_qty);
    frm.set_value('total', total_amt);
    frm.set_value('net_total', total_amt);
}

// ðŸ§¾ Update totals on Save too
frappe.ui.form.on('Proforma Invoice', {
    before_save: function (frm) {
        calculate_totals(frm);
    },
    items_remove: function(frm) {
        calculate_totals(frm);
    }
});

frappe.ui.form.on('Proforma Invoice', {
    sales_taxes_and_charges_template: function(frm) {
        if (frm.doc.sales_taxes_and_charges_template) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Sales Taxes and Charges Template",
                    name: frm.doc.sales_taxes_and_charges_template
                },
                callback: function(r) {
                    if (r.message) {
                        const template = r.message;

                        // Clear current taxes table
                        frm.clear_table("sales_taxes_and_charges");

                        // Loop over tax rows from the template
                        (template.taxes || []).forEach(function(row) {
                            let new_row = frm.add_child("sales_taxes_and_charges");
                            new_row.charge_type = row.charge_type;
                            new_row.account_head = row.account_head;
                            new_row.description = row.description;
                            new_row.cost_center = row.cost_center;
                            new_row.rate = row.rate;
                            new_row.included_in_print_rate = row.included_in_print_rate;
                        });

                        frm.refresh_field("sales_taxes_and_charges");
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Sales Taxes and Charges', {
    rate: function(frm, cdt, cdn) {
        calculate_cumulative_tax(frm);
    }
});

frappe.ui.form.on('Proforma Invoice', {
    validate: function(frm) {
        calculate_cumulative_tax(frm);
    }
});

async function calculate_cumulative_tax(frm) {
    let net_total = frm.doc.net_total || 0;
    let total_taxes = 0;
    let previous_total = net_total;

    for (const row of frm.doc.sales_taxes_and_charges || []) {
        if (row.rate) {
            // Always use net_total to calculate amount
            const amount = (row.rate / 100) * net_total;

            // First row: total = net_total + amount
            // Next rows: total = previous_total + amount
            const row_total = previous_total + amount;

            await frappe.model.set_value(row.doctype, row.name, 'tax_amount', amount);
            await frappe.model.set_value(row.doctype, row.name, 'total', row_total);

            previous_total = row_total;
            total_taxes += amount;
        }
    }

    frm.set_value("total_taxes_and_charges", total_taxes);
    frm.set_value("grand_total", previous_total);
    frm.refresh_field("sales_taxes_and_charges");
}

frappe.ui.form.on('Material Request', {
    custom_material_request_terms: function(frm) {
        if (frm.doc.custom_material_request_terms) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Material Request Document Required Template",
                    name: frm.doc.custom_material_request_terms
                },
                callback: function(r) {
                    if (r.message) {
                        const template = r.message;

                        // Clear existing rows in the child table
                        frm.clear_table("custom_terms_and_conditions");

                        // Loop through rows from template and add to current form
                        (template.terms || []).forEach(function(row) {  // use the correct child table fieldname
                            let new_row = frm.add_child("custom_terms_and_conditions");
                            new_row.terms = row.terms;
                            new_row.terms_value = row.terms_value;
                        });

                        frm.refresh_field("custom_terms_and_conditions");
                    }
                }
            });
        }
    }
});
