script:
// ðŸ”„ Recalculate amount when qty or rate is changed
frappe.ui.form.on('Proforma Invoice Item', {
    quantity: function (frm, cdt, cdn) {
        calculate_amount(frm, cdt, cdn);
        calculate_totals(frm); // also update parent
    },
    rate: function (frm, cdt, cdn) {
        calculate_amount(frm, cdt, cdn);
        calculate_totals(frm);
    },
    amount: function(frm, cdt, cdn) {
        calculate_totals(frm);  // if amount is edited manually
    }
});

// ðŸ“¦ Calculate line amount
function calculate_amount(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    row.amount = (row.quantity || 0) * (row.rate || 0);
    frappe.model.set_value(cdt, cdn, 'amount', row.amount);
}

// ðŸ” Calculate parent totals
function calculate_totals(frm) {
    let total_qty = 0;
    let total_amt = 0;

    (frm.doc.custom_items || []).forEach(row => {
        total_qty += row.quantity || 0;
        total_amt += row.amount || 0;
    });

    // Show in UI
    frm.set_value('total_quantity', total_qty);
    frm.set_value('total', total_amt);
    frm.set_value('net_total', total_amt);
}

// ðŸ§¾ Update totals on Save too
frappe.ui.form.on('Proforma Invoice', {
    before_save: function (frm) {
        calculate_totals(frm);
    },
    items_remove: function(frm) {
        calculate_totals(frm);
    }
});

frappe.ui.form.on('Proforma Invoice', {
    sales_taxes_and_charges_template: function(frm) {
        if (frm.doc.sales_taxes_and_charges_template) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Sales Taxes and Charges Template",
                    name: frm.doc.sales_taxes_and_charges_template
                },
                callback: function(r) {
                    if (r.message) {
                        const template = r.message;

                        // Clear current taxes table
                        frm.clear_table("sales_taxes_and_charges");

                        // Loop over tax rows from the template
                        (template.taxes || []).forEach(function(row) {
                            let new_row = frm.add_child("sales_taxes_and_charges");
                            new_row.charge_type = row.charge_type;
                            new_row.account_head = row.account_head;
                            new_row.description = row.description;
                            new_row.cost_center = row.cost_center;
                            new_row.rate = row.rate;
                            new_row.included_in_print_rate = row.included_in_print_rate;
                        });

                        frm.refresh_field("sales_taxes_and_charges");
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Sales Taxes and Charges', {
    rate: function(frm, cdt, cdn) {
        calculate_cumulative_tax(frm);
    }
});

frappe.ui.form.on('Proforma Invoice', {
    validate: function(frm) {
        calculate_cumulative_tax(frm);
    }
});

async function calculate_cumulative_tax(frm) {
    let net_total = frm.doc.net_total || 0;
    let total_taxes = 0;
    let previous_total = net_total;

    for (const row of frm.doc.sales_taxes_and_charges || []) {
        if (row.rate) {
            // Always use net_total to calculate amount
            const amount = (row.rate / 100) * net_total;

            // First row: total = net_total + amount
            // Next rows: total = previous_total + amount
            const row_total = previous_total + amount;

            await frappe.model.set_value(row.doctype, row.name, 'tax_amount', amount);
            await frappe.model.set_value(row.doctype, row.name, 'total', row_total);

            previous_total = row_total;
            total_taxes += amount;
        }
    }

    frm.set_value("total_taxes_and_charges", total_taxes);
    frm.set_value("grand_total", previous_total);
    frm.refresh_field("sales_taxes_and_charges");
}
