css:
.print-format th {
    background-color: transparent !important;
    border-bottom: 1px solid !important;
    border-top: none !important;
}
.print-format .ql-editor {
    padding-left: 0px;
    padding-right: 0px;
}

.print-format table {
    margin-bottom: 0px;
    }
.print-format .table-data tr:last-child { 
    border-bottom: 1px solid !important;
}

.print-format .table-inner tr:last-child {
    border-bottom:none !important;
}
.print-format .table-inner {
    margin: 0px 0px;
}

.print-format .table-data ul li { 
    color:#787878 !important;
}

.no-top-border {
    border-top:none !important;
}

.table-inner td {
    padding-left: 0px !important;    
    padding-top: 1px !important;
    padding-bottom: 1px !important;
    color:#787878 !important;
}

.total {
    background-color: lightgrey !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

.qr-code{
    float:right;
}

.invoice-heading {
  margin: 0;
}

.ksa-invoice-table {
  border: 1px solid #888a8e;
  border-collapse: collapse;
  width: 100%;
  margin: 20px 0;
  font-size: 16px;
}

.ksa-invoice-table.two-columns td:nth-child(2) {
  direction: rtl;
}

.ksa-invoice-table th {
  border: 1px solid #888a8e;
  max-width: 50%;
  padding: 8px;
}

.ksa-invoice-table td {
  padding: 5px;
  border: 1px solid #888a8e;
  max-width: 50%;
}

.ksa-invoice-table thead,
.ksa-invoice-table tfoot {
  text-transform: uppercase;
}

.qr-rtl {
  direction: rtl;
}

.qr-flex{
    display: flex;
    justify-content: space-between;
}

.table-container {
    width: 100%;
}

table {
    width: 100%;
    border-collapse: collapse;
}

.company{
    margin:40px !important;
    width:65.5%;
}


th{
    background-color:none !important;
}

.center-img {

        margin: auto;
    }



td {
    border: 1px solid #ccc;
    padding: 10px;
    text-align:left;
}

.small-part {
    width: 22%;
    margin-top:20px;
}

.big-part {
    width: 58%;
}

.small-part-with-qr {
    width: 20%;
    position: relative;
}

.qr-code {
    margin-bottom: 5px;
}

.qr-text {
    font-size: 12px;
}

.qr-code img {
    max-width: 100%;
    height: auto;
    display: block;
    margin: 0 auto;
}

.invoice,
.vehicle-no {
    width: 30%;
    text-align:left;
}

.details {
    width: 20%;
    text-align:left;
}
